qemu-system-x86_64 -drive if=pflash,format=raw,unit=0,file=/usr/share/OVMF/OVMF_CODE.fd,readonly=on -drive if=pflash,format=raw,unit=1,file=/home/path/to/local/OVMF_VARS.fd -enable-kvm -machine q35 -cpu host -smp cores=2,threads=2,sockets=1 -boot menu=on -drive if=virtio,format=qcow2,file=/home/path/to/local/disk.qcow2,discard=unmap -m 3G -netdev user,id=net0 -device virtio-net-pci,netdev=net0 -vga virtio -display gtk,gl=on
